from Crypto.Util.number import getPrime, bytes_to_long
from random import randint
import gmpy2

FLAG = b"Securinets{fake_flag}"
flag1 = FLAG[:15]
flag2 = FLAG[15:]

def crypto1():
    p = getPrime(1024)
    q = getPrime(1024)
    n = p * q
    e = 0x10001
    x1 = randint(0, 2**11)
    y1 = randint(0, 2**114)
    x2 = randint(0, 2**11)
    y2 = randint(0, 2**514)
    hint1 = x1 * p + y1 * q - 0x114
    hint2 = x2 * p + y2 * q - 0x514
    c = pow(bytes_to_long(flag1), e, n)
    return n, c, hint1, hint2

def crypto2():
    p = getPrime(1024)
    q = getPrime(1024)
    n = p * q
    e = 0x10001
    base = 514 * p - 114 * q
    if gmpy2.gcd(base, n) != 1:
        return crypto2()
    hint = pow(base, n - p - q, n)
    c = pow(bytes_to_long(flag2), e, n)
    return n, c, hint

def main():
    
    n1, c1, hint1, hint2 = crypto1()
    print(f"n = {n1}")
    print(f"c = {c1}")
    print(f"hint1 = {hint1}")
    print(f"hint2 = {hint2}")
    n2, c2, hint = crypto2()
    print(f"n = {n2}")
    print(f"c = {c2}")
    print(f"hint = {hint}")
    

if __name__ == "__main__":
    main()

